import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom'; // Import useParams from react-router-dom

function AddProduct() {
    const { id } = useParams(); // Get the product ID from URL parameters
    const nvigate=useNavigate()
    const [formData, setFormData] = useState({
        name: '',
        image: '',
        size: '',
        color: '',
        brand: ''
    });

    useEffect(() => {
        if (id) { // If ID is available, fetch the product data
            fetchProduct(id);
        }
    }, [id]);

    const fetchProduct = async (productId) => {
        try {
            const response = await axios.get(`http://localhost:3000/products/${productId}`);
            setFormData(response.data);
        } catch (error) {
            console.error('Error fetching product data:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleAddProduct = async () => {
        try {
            if (id) {
                await axios.put(`http://localhost:3000/products/${id}`, formData); // Use PUT for updating existing product
            } else {
                await axios.post('http://localhost:3000/products', formData); // Use POST for adding new product
            }
            
        } catch (error) {
            console.error('Error adding/updating product:', error);
        }
    };

    return (
        <div className="container mt-4">
            <h2>{id ? 'Edit Product' : 'Add Product'}</h2>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input type="text" id="name" name="name" className="form-control" value={formData.name} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="image" className="form-label">Image URL:</label>
                    <input type="text" id="image" name="image" className="form-control" value={formData.image} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="size" className="form-label">Size:</label>
                    <input type="text" id="size" name="size" className="form-control" value={formData.size} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="color" className="form-label">Color:</label>
                    <input type="text" id="color" name="color" className="form-control" value={formData.color} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="brand" className="form-label">Brand:</label>
                    <input type="text" id="brand" name="brand" className="form-control" value={formData.brand} onChange={handleChange} />
                </div>
                <button onClick={handleAddProduct} className="btn btn-primary">{id ? 'Update' : 'Add'}</button>
        </div>
    );
}

export default AddProduct;
