    const [detail, setDetail] = useState([]);
    let { id } = useParams();

    function productDetail() {
        axios.get("http://localhost:3000/products/" + id)
            .then((res) => {
                console.log(res.data);
                setDetail(res.data);
            })
            .catch((error) => {
                console.error('Error fetching product detail:', error);
            });
    }

    useEffect(() => {
        if (id) {
            productDetail();
        }
    }, [id]);

