import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegistrationForm = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginForm, setIsLoginForm] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  let navigate = useNavigate();

  const handleToggleForm = () => {
    setIsLoginForm(!isLoginForm);
    setErrorMessage('');
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Simple validation
    if (!username.trim() || !email.trim() || !password.trim()) {
      setErrorMessage('Please fill out all fields.');
      return;
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email.trim())) {
      setErrorMessage('Please enter a valid email address.');
      return;
    }

   const senddata={
    username:username,
      email:email,
      password:"password"
    }

   axios.post("http://localhost:3000/users",senddata).then((res)=>{
    console.log();
   }).catch((err)=>{
    console.log(err);
   })
   
  };

  return (
    <div className="container mt-5 row">
      <div className="col-lg-4"></div>
      <div className="col-lg-4">
        <div className="form-container">
          {isLoginForm ? (
            <>
              <h2>Login</h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email:</label>
                  <input
                    type="email"
                    id="email"
                    className="form-control"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Password:</label>
                  <input
                    type="password"
                    id="password"
                    className="form-control"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>

                {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

                <button  className="btn btn-primary" onClick={()=>{handlelogin()}}>Login</button>
              </form>
              <button onClick={handleToggleForm} className="btn btn-link">Switch to Registration</button>
            </>
          ) : (
            <>
              <h2>Register</h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="username" className="form-label">Username:</label>
                  <input
                    type="text"
                    id="username"
                    className="form-control"
                    name="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email:</label>
                  <input
                    type="email"
                    id="email"
                    className="form-control"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Password:</label>
                  <input
                    type="password"
                    id="password"
                    className="form-control"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>

                {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

                <button type="submit" className="btn btn-primary">Register</button>
              </form>
              <button onClick={handleToggleForm} className="btn btn-link">Switch to Login</button>
            </>
          )}
        </div>
      </div>
      <div className="col-lg-4"></div>
    </div>
  );
};

export default RegistrationForm;
