import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'

function Detail() {
    const [detail, setDetail] = useState(null); // Change initial state to null
    let { id } = useParams();

    function productdetail() {
        axios.get("http://localhost:3000/products")
        .then((res) => {
            const product = res.data.find(item => item.id === parseInt(id)); // Find the product by ID
            setDetail(product);
        })
    }

    useEffect(() => {
        if (id) {
            productdetail();
        }
    }, [id]);

    if (!detail) {
        return <div>Loading...</div>; // Show a loading state until the product details are fetched
    }

    return (
        <>
            <div className='menBanner'>
                <div className="breadcrumbs">
                    <div className="container">
                        <div className="row d-flex justify-content-between align-items-center">
                            <div className="col-lg-5">
                                <p className="bread"><span><a href="index.html">Home</a></span> / <span>Product Details</span></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div>
                <img className='' src={detail.image} alt={detail.title} />
                <h1>{detail.title}</h1>
                <h2>{detail.price}</h2>
                <p>{detail.description}</p>
            </div>
        </>
    )
}

export default Detail;
