import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'

function Detail() {
    const [detail, setDetail] = useState(null)
    let { id } = useParams()

    function productdetail() {
        axios.get(`http://localhost:3000/products/${id}`)
        .then((res) => {
            console.log(res.data);
            setDetail(res.data)
        })
        .catch((error) => {
            console.error('Error fetching the product detail:', error);
        });
    }

    useEffect(() => {
        if (id) {
            productdetail()
        }
    }, [id]) // Added id as dependency to refetch if id changes

    return (
        <>
            <div className='menBanner'>
                <div className="breadcrumbs">
                    <div className="container">
                        <div className="row d-flex justify-content-between align-items-center">
                            <div className="col-lg-5">
                                <p className="bread"><span><a href="/">Home</a></span> / <span>Product Details</span></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div>
                {detail ? (
                    <>
                        <h1>{detail.price}</h1>
                        <img src={detail.image} alt="Product" />
                    </>
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </>
    )
}

export default Detail
