import React, { useEffect, useState } from 'react';
import Productcard from './Productcard';
import './Product.css';
import { useNavigate } from 'react-router';
import axios from 'axios';

export default function Products() {
    const [newdata, setNewdata] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [filters, setFilters] = useState({ size: '', color: '', brand: '', sort: '' });

    let navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            try {
                const res = await axios.get("http://localhost:3000/products");
                console.log(res.data);
                setNewdata(res.data);
                setFilteredProducts(res.data); // Initialize filtered products with fetched data
            } catch (error) {
                console.error("Error fetching data", error);
            }
        }
        fetchData();
    }, []);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters({
            ...filters,
            [name]: value
        });
    };

    const applyFilters = () => {
        let filtered = [...newdata];

        if (filters.size) {
            filtered = filtered.filter(product => product.size === filters.size);
        }

        if (filters.color) {
            filtered = filtered.filter(product => product.color === filters.color);
        }

        if (filters.brand) {
            filtered = filtered.filter(product => product.brand === filters.brand);
        }

        if (filters.sort) {
            filtered = filtered.sort((a, b) => {
                if (filters.sort === 'asc') {
                    return parseFloat(a.price) - parseFloat(b.price);
                } else {
                    return parseFloat(b.price) - parseFloat(a.price);
                }
            });
        }

        setFilteredProducts(filtered);
    };

    return (
        <>
            <div className="row">
                <div className="col-lg-3 mt-2 filter-section">
                    <h2>Filter</h2>
                    <div className="filter-group">
                        <h4>Size</h4>
                        <select name="size" onChange={handleFilterChange}>
                            <option value="">All</option>
                            <option value="7">7</option>
                            <option value="8">8</option>
                        </select>
                    </div>
                    <div className="filter-group">
                        <h4>Color</h4>
                        <select name="color" onChange={handleFilterChange}>
                            <option value="">All</option>
                            <option value="red">Red</option>
                            <option value="blue">Blue</option>
                            <option value="green">Green</option>
                            <option value="yellow">Yellow</option>
                        </select>
                    </div>
                    <div className="filter-group">
                        <h4>Brand</h4>
                        <select name="brand" onChange={handleFilterChange}>
                            <option value="">All</option>
                            <option value="sparx">Sparx</option>
                            <option value="adidas">Adidas</option>
                            <option value="woodland">Woodland</option>
                            <option value="red-chief">Red Chief</option>
                        </select>
                    </div>
                    <div className="filter-group">
                        <h4>Sort by Price</h4>
                        <select name="sort" onChange={handleFilterChange}>
                            <option value="">None</option>
                            <option value="asc">Low to High</option>
                            <option value="desc">High to Low</option>
                        </select>
                    </div>
                    <button onClick={applyFilters}>Apply Filters</button>
                </div>
                <div className="col-lg-8 text-center product-section">
                    <h2 className='mb-4'></h2>
                    <section>
                        <div className='row justify-content-center'>
                            {filteredProducts.map((item, index) => (
                                <Productcard
                                    key={index}
                                    img={item.image}
                                    title={item.name}
                                    text={item.description}
                                    price={item.price}
                                />
                            ))}
                        </div>
                    </section>
                </div>
            </div>
        </>
    );
}
